import React, { useState } from 'react'
import axios from 'axios'

const Login = () => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const submit = async e => {
        e.preventDefault()
        const user = {
            username:username,
            password:password
        };

        const config = {
            headers: {
                'Content-Type':'application/json'
            },
            withCredentials: true
        };
        const {data} = await axios.post('http://localhost:8000/token/', user,config)
        localStorage.clear()
        console.log(data.access)
        localStorage.setItem('access_token', data.access)
        localStorage.setItem('refresh_token', data.refresh)
        axios.defaults.headers.common['Authorization'] = `Bearer ${data['access']}`
        window.location.href='/'
    }
    return (
        <>
        <Modal>
        <Modal.Header closeButton>
          <Modal.Title>Вход</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId='formBasicEmail'>
              <Form.Label>Почта</Form.Label>
              <Form.Control 
                type='email' 
                placeholder='Введите почту' 
                value={loginEmail}
                onChange={(e) => setUsername(e.target.value)} // Обновляем состояние при вводе
              />
            </Form.Group>
            <Form.Group controlId='formBasicPassword'>
              <Form.Label>Пароль</Form.Label>
              <Form.Control type='password' 
                placeholder='Введите пароль' 
                value={loginPassword}
                onChange={(e) => setPassword(e.target.value)} // Обновляем состояние при вводе
              />
            </Form.Group>
            <Button variant='primary' style={{ marginTop: '8px' }} onClick={handleLogin}>
              Войти
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

        </>
    )
}

export default Login
